sourcesContent:
// theming

// https://www.smashingmagazine.com/2016/04/web-developer-guide-color/
// http://paletton.com/

:root
{
	--base-color            : #fa9c20;
	--base-color-lighter    : #FFB24B;
	--base-color-lightest   : #FFC474;
	--base-color-darker     : #D07904;
	--base-color-darkest    : #A45E00;

	--accent-color          : #03B2CB;
	--accent-color-lighter  : #53D5E7;
	--accent-color-lightest : #94EBF7;
	--accent-color-darker   : #018395;
	--accent-color-darkest  : #016674;

	--gray-color-lightest   : #FAF8F6;
	--gray-color-lighter    : #f6f1ec;
	--gray-color-lighter-   : #E8DED3;
	--gray-color-light      : #E0D3C5;
	--gray-color            : #c4bfb8;
	--gray-color-dark       : #B3AA9F;
	--gray-color-darker     : #918d88;

	--black-color           : #4e3d27;

	--white-color        : #ffffff;

	--error-color         : #EB2010;
	--error-color-lighter : #FF1300;
	--error-color-darker  : #D30F00;

	--input-border-width         : 2pt;
	--input-separator-width      : 1pt;
	--input-padding              : 0.15rem;

	--dropdown-item-side-padding : 4; // im modular grid units

	--header-vertically-centered-text-margin-top: 0.1em;

	--border-radius : 3pt;

	--modular-grid-unit : 0.3rem;

	--card-border-radius : 0.2rem;

	--shadow : 0 2px 5px 0 rgba(0,0,0,0.16), 0 2px 10px 0 rgba(0,0,0,0.12);
}

$extra-large-screen-min-width : 1260pt;
$large-screen-min-width       : 680pt;
$medium-screen-min-width      : 460pt;
$small-screen-min-width       : 320pt;

// Shows all ".rich" children with the exception
// of ".rich > .rich-fallback" elements
body.javascript-is-enabled
{
	.rich-fallback
	{
		display: none !important;
	}
}

// Shows only ".rich > .rich-fallback" children
// of ".rich" elements
body:not(.javascript-is-enabled)
{
	.rich
	{
		> *:not(.rich-fallback)
		{
			display: none !important;
		}
	}
}

@import "constants.scss";

.content-section
{
	background-color : var(--white-color);
	border-bottom    : 1px solid var(--gray-color);
}

.content-section--no-padding
{
	// padding : 0;

	.content-section-header
	{
		margin-bottom : 0;
		margin-left   : 0;
		margin-right  : 0;
	}
}

.content-section--busy
{
	cursor: wait;
}

.content-section-header
{
	padding-top      : 0.5em;
	padding-bottom   : 0.45em;
	border-bottom    : 1pt solid var(--gray-color-light);
	font-size        : 1.3rem;
	text-align       : left;
	background-color : var(--gray-color-lightest);
}

.content-section-divider
{
	position         : absolute;
	top              : 0;
	height           : 1pt;
	background-color : var(--gray-color-light);
}

// // This will be replaced with the contents of the mixin
// // when CSS native variables will be supported in all major browsers.
// // http://caniuse.com/#search=css%20var
// @include set_css_variable_header_height(var(--header-height));

.card, .dropdown-item-list-expanded
{
	box-shadow: var(--shadow);
}

header
{
	//position : fixed;
	//top      : 0;
	//left     : 0;
	//right    : 0;
	//z-index  : 2;
    //
	//display        : flex;
	//flex-direction : row;
	//align-items    : center;
    //
	//padding-left  : 1em;
	//padding-right : 1em;
    //
	//background-color : var(--base-color-darker);

	// border-bottom : 1px solid $header-border-color;
	// box-sizing    : border-box;

	.sign-out--fallback, .button__link, a, .button button, .dropdown-item-selected
	{
		color: var(--white-color);

		&:active
		{
			color: var(--base-color-lightest);
		}
	}

	.logo
	{
		color: var(--white-color);
	}
}

section
{
	background-color : var(--white-color);
}

footer
{
	// can be removed later
	position: relative;

	// border-top: 1px solid $footer-border-color;

	padding-top    : 1.3em;
	padding-bottom : 1.2em;

	padding-left  : 1em;
	padding-right : 1em;

	font-size: 0.8em;

	> div
	{
		text-align: center;
	}
}

.content
{
	flex-grow   : 1;
	flex-shrink : 0;
	flex-basis  : auto;
}

.nav-collapsible
{
	transform  : translate3d(-100%, 0, 0);
	transition : transform 120ms ease-out;
	opacity    : 0.9;
}

.menu-item-selected
{
	color : var(--base-color-lightest);
}

// This technique drops IE10-11 support.
// https://github.com/philipwalton/flexbugs#3-min-height-on-a-flex-container-wont-apply-to-its-flex-items
// They say it works in Edge though.
//
// Also "100vh" seems to introduce a window resizing bug in Chrome
// when window height is max of all previous window heights.
//
.page
{
	display        : flex;
	flex-direction : column;

    min-height: 100vh;
}

.row
{
	&:after
	{
		content : "";
		display : table;
		clear   : both;
	}
}

.row--content-sections + .row--content-sections
{
	position : relative;
}

// styling react-modal dialog widget

// when changing css transition times here
// also change `default_close_timeout` in ./components/modal.js accordingly

.ReactModal__Overlay
{
	opacity: 0;
	// "auto" introduces a vertical scrollbar when "bouncing" (animation)
	// overflow: auto;
	overflow: hidden;
}

.ReactModal__Overlay--after-open
{
	opacity: 1;
}

.ReactModal__Overlay--before-close
{
	opacity: 0;
}

.ReactModal__Overlay--after-open,
.ReactModal__Overlay--before-close
{
	transition: opacity 100ms ease-out;
}

.ReactModal__Content
{
	outline: none;
}

.ReactModal__Content
{
	transform: scale(0.8);
}

.ReactModal__Content--after-open
{
	transform: scale(1);
}

.ReactModal__Content--before-close
{
	transform: scale(0.8);
}

.ReactModal__Content--after-open,
.ReactModal__Content--before-close
{
	transition: transform 120ms ease-out;
}

// hides `react-modal` scrollbar when closing the modal
.ReactModal__Overlay--before-close
{
	overflow: hidden;
}

// .ReactModal__Body--open
// {
// 	// disables page scrolling when modal is presented,
// 	// but introduces page width jumps.
// 	height: 100%;
// 	overflow: hidden;
// }

// end react-modal stying

.modal-header--separated
{
	border-bottom: 1px solid #e0e0e0;
}

.modal-actions--separated
{
	border-top: 1px solid #e0e0e0;
}

.modal-header
{
	padding-left   : calc(4 * var(--modular-grid-unit));
	padding-right  : calc(4 * var(--modular-grid-unit));
	padding-top    : calc(3 * var(--modular-grid-unit)); // 4
	padding-bottom : calc(3 * var(--modular-grid-unit)); // 3.5

	border-top-left-radius  : var(--card-border-radius);
	border-top-right-radius : var(--card-border-radius);
}

.modal-actions
{
	padding-left   : calc(4 * var(--modular-grid-unit));
	padding-right  : calc(2 * var(--modular-grid-unit));
	padding-top    : calc(1.5 * var(--modular-grid-unit));
	padding-bottom : calc(1.5 * var(--modular-grid-unit));

	border-bottom-left-radius  : var(--card-border-radius);
	border-bottom-right-radius : var(--card-border-radius);

	.button
	{
		margin-right : calc(2 * var(--modular-grid-unit));

		&:last-child
		{
			margin-right : 0;
		}
	}
}

.modal-content--no-bars
{
	padding-top   : calc(4 * var(--modular-grid-unit));
	border-radius : var(--card-border-radius);
}

.modal--could-not-close-because-busy
{
	animation: bounce_z 0.6s;
	transform: translate3d(0, 0, 0);
}

// https://daneden.github.io/animate.css/
// https://github.com/daneden/animate.css/blob/master/source/bouncing_entrances/bounceIn.css
@keyframes bounce_z
{
	from, 25%, 47%, 72%, to
	{
		animation-timing-function: cubic-bezier(0.215, 0.610, 0.355, 1.000);
	}

	0%
	{
		transform: scale3d(1, 1, 1);
	}

	25%
	{
		transform: scale3d(.97, .97, .97);
	}

	47%
	{
		transform: scale3d(1.025, 1.025, 1.025);
	}

	72%
	{
		transform: scale3d(0.99, 0.99, 0.99);
	}

	to
	{
		transform: scale3d(1, 1, 1);
	}
}

