    .fontSize-pad2-h(@content-font-pad2-h-lg-content);
    .fontSize-pad2-v(@content-font-pad2-v-lg-content);
    //笔记本
    .fontSize-lg-xl(@content-font-lg-xl-lg-content);
    .fontSize-lg-chrome(@content-font-lg-chrome-lg-content);

    ///////////////////////////////////////////////////////////////
    .element-preperty .boot-min(font-size;@content-font-5scl-v-lg-content);
    .element-preperty .boot-xs-min(font-size;@content-font-5scl-v-lg-content);
