".clearfix() {
  &:before,
  &:after {
    content: " "; // 1
    display: table; // 2
  }
  &:after {
    clear: both;
  }
}
"

"// Framework grid generation
//
// Used only by Bootstrap to generate the correct number of grid classes given
// any value of `@grid-columns`.

.make-grid-columns-extents() {
  // Common styles for all sizes of grid columns, widths 1-12
  .col(@index) { // initial
    @item: ~".col-xs6-@{index}, .col-xp-@{index}, .col-xs5-@{index}, .col-xs4-@{index}";
    .col((@index + 1), @item);
  }
  .col(@index, @list) when (@index =< @grid-columns) { // general; "=<" isn't a typo
    @item: ~".col-xs6-@{index}, .col-xp-@{index}, .col-xs5-@{index}, .col-xs4-@{index}";
    .col((@index + 1), ~"@{list}, @{item}");
  }
  .col(@index, @list) when (@index > @grid-columns) { // terminal
    @{list} {
      position: relative;
      min-height: 1px;
    }
  }
  .col(1); // kickstart it
}


"

"
// Generate the extra small columns

.make-xp-column(@columns; @gutter: @grid-gutter-width) {
  position: relative;
  float: left;

  width: percentage((@columns / @grid-columns));
  min-height: 1px;
  padding-left:  (@gutter / 2);
  padding-right: (@gutter / 2);
}
.make-xp-column-offset(@columns) {
  margin-left: percentage((@columns / @grid-columns));
}
.make-xp-column-push(@columns) {
  left: percentage((@columns / @grid-columns));
}
.make-xp-column-pull(@columns) {
  right: percentage((@columns / @grid-columns));
}

//1200~1439 desktop
.make-dt-column(@columns; @gutter: @grid-gutter-width) {
  position: relative;
  float: left;

  width: percentage((@columns / @grid-columns));
  min-height: 1px;
  padding-left:  (@gutter / 2);
  padding-right: (@gutter / 2);
}
.make-dt-column-offset(@columns) {
  margin-left: percentage((@columns / @grid-columns));
}
.make-dt-column-push(@columns) {
  left: percentage((@columns / @grid-columns));
}
.make-dt-column-pull(@columns) {
  right: percentage((@columns / @grid-columns));
}
